import Vue from 'vue';
var cache = Vue.observable({});
export function setResults(path, res) {
  return Vue.set(cache, path, res);
}
export function getResults(path) {
  return cache[path];
}
export function clearAllResults(currentPath) {
  for (var path in cache) {
    if (path !== currentPath) {
      Vue.delete(cache, path);
    }
  }
}
export function formatError(err, route) {
  var matched = route.matched[0];
  var options = matched ? matched.components.default : {};

  if (err.stringified && options.__file) {
    console.error("An error occurred while executing " + "query for ".concat(options.__file, "\n\n") + "Error: ".concat(err.stringified));
  }
}
